PS> $NewAgentAsset = @{
    'Assets' =@(
        @{
        'name'='x2test'
        "asset_type_id" = 5
        'location_id' = 1
        'asset_type_name' = "Computer with Dell Agent"
        }
    )
}

PS> New-SmaAsset -Body $NewAgentAsset

$taskparams = @{
'attempts' = 2
    'onFailure' = 'break'
    'onRemediationFailure' = @(
        @{
            'id'= 27 # status ID
            'params'= [ordered]@{
                'type'='status'
                'message'='this is a test message'
            }
        }
    )
}

New-SmaScriptTask -ScriptID 1234 -Body $taskparams

PS> $NewTicket = @{
